<?phpclass admin_student extends CI_Controller {    public function __construct()    {        parent::__construct();        if(!$this->session->userdata('is_logged_in')){            redirect('login');        }    }    public function profile()    {                $id = $this->uri->segment(3);        if ($this->input->server('REQUEST_METHOD') === 'POST')        {                $data_to_store = array(                    'birthday' => $this->input->post('birthday'),                    'email_addres' => $this->input->post('email_addres'),                    'address' => $this->input->post('address'),                    'major' => $this->input->post('major'),                    'advisor' => $this->input->post('advisor'),                );                if($this->Admin_model->update_user($data_to_store) == TRUE){                    $this->session->set_flashdata('flash_message', 'updated');                }else{                    $this->session->set_flashdata('flash_message', 'not_updated');                }                redirect('sprofile');                    }        $usernamex = $this->session->userdata('user_name');        $data['userxx'] = $this->Admin_model->get_user2($usernamex);        $data['advisorx'] = $this->courses_model->get_advisors();        $data['main_content'] = 'admin/student/profile';        $this->load->view('admin/includes/template', $data);      }    }